#include<bits/stdc++.h>
using namespace std;
int n,q,timess = 100,cache,seed = 239847;
string s;
int main(){
	//freopen(".in","r",stdin);
	freopen("diff.in","w",stdout);
	s = "";
	cin>>q;
	n = s.length();
	cout<<n<<endl<<s<<endl<<timess<<endl;
	for(int i = 0;i < seed;i++){
		cache = rand();
	}
	for(int i = 0;i < timess;i++){
		if(rand()%2 == 1){
			cout<<2<<" ";
			cache = rand()%n+1;
			cout<<cache<<" ";
			if(cache+5 < n){
				cout<<cache+5<<endl;
			}else{
				cout<<cache+1<<endl;
			}
		}else{
			cout<<1<<" ";
			cache = rand()%n+1;
			cout<<cache<<" "<<char((rand()%26)+97)<<endl;
		}
	}
	return 0;
}

